import UIKit

// MARK: Connect View, Interactor, and Presenter

extension ResultInteractor: ResultViewControllerOutput, ResultRouterDataSource, ResultRouterDataDestination {
}

extension ResultPresenter: ResultInteractorOutput {
}

class ResultConfigurator {
    // MARK: Object lifecycle
    
    static let sharedInstance = ResultConfigurator()
    
    private init() {}
    
    // MARK: Configuration
    
    func configure(viewController: ResultViewController) {
        
        let presenter = ResultPresenter()
        presenter.output = viewController
        
        let interactor = ResultInteractor()
        interactor.output = presenter
        
        let router = ResultRouter(viewController: viewController, dataSource: interactor, dataDestination: interactor)
        
        viewController.output = interactor
        viewController.router = router
    }
}
