import UIKit

protocol ResultViewControllerInput {
    
}

protocol ResultViewControllerOutput {
    func showResult()
}

class ResultViewController: UIViewController, ResultViewControllerInput {
    
    var output: ResultViewControllerOutput?
    var router: ResultRouter?
    @IBOutlet weak var resultLabel: UILabel!
    // MARK: Object lifecycle
    
    override func awakeFromNib() {
        super.awakeFromNib()
        ResultConfigurator.sharedInstance.configure(viewController: self)
        
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        output?.showResult()
    }
    
    // MARK: Requests
    
    // MARK: Display logic
    func showResult(_ result: String) {
        resultLabel.text = result
    }
}

//This should be on configurator but for some reason storyboard doesn't detect ViewController's name if placed there
extension ResultViewController: ResultPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router?.passDataToNextScene(for: segue)
    }
}
